package programs;

public class Largestinanarray {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int range[] = {33,4,5};
		int max = range[0];
		
		for (int i = 0;i<range.length;i++)
		{
			if (range[i]>max)
			{
				max = range[i];
			}}
				System.out.println(max);
				
				
			
		}

	}

